import React from 'react'
import PropTypes from 'prop-types'
import LazyLoad from 'react-lazyload';
import { Fade } from 'react-reveal';
import { Parallax } from 'react-scroll-parallax';
import Image from './Image'

const LazyImage = (props) => (
    <LazyLoad height={ 200 } offset={ 500 }>
        <Fade top distance={ '20px' }>
            <Image {...props}/>
        </Fade>
    </LazyLoad>
)

export const LazyParalexImage = ({ y, ...props }) => (
    <LazyLoad height={ 200 } offset={ 500 }>
        <Parallax y={ y }>
            <Fade top distance={ '20px' }>
                <Image {...props}/>
            </Fade>
        </Parallax>
    </LazyLoad>
)

export const LazyFadeImage = ({ revealProps, image, baseClass, alt }) => (
    <LazyLoad height={ 200 } offset={ 500 }>
        <Fade {...revealProps}>
            <img
                className={ `${baseClass}__image` }
                srcSet={ `
                    ${image.imageS} 768w,
                    ${image.imageM} 1200w,
                    ${image.imageL} 1440w,
                ` }
                sizes="(max-width: 768px) 700px,
                    (max-width: 1200px) 1000px,
                    1440px"
                src={ image.imageL }
                alt={ alt }
            />
        </Fade>
    </LazyLoad>
)

LazyFadeImage.defaultProps = {
    image: {},
    alt: '',
    baseClass: '',
    revealProps: {
        bottom: true,
        opposite: true,
        delay: 100,
        duration: 500,
        distance: "1000px"
    }
}

LazyFadeImage.propTypes = {
    image: PropTypes.shape({
        imageS: PropTypes.string,
        imageM: PropTypes.string,
        imageL: PropTypes.string,
    }),
    alt: PropTypes.string,
    baseClass: PropTypes.string,
    revealProps: PropTypes.shape({
        delay: PropTypes.number,
        bottom: PropTypes.bool,
        left: PropTypes.bool,
        duration: PropTypes.number,
        distance: PropTypes.string,
    }),
}

export default LazyImage;
