import React, { useContext } from 'react'
import { DataContext } from '../pages/_app';

const Footer = () => {
    const { labels, footer, page } = useContext(DataContext)
    const { socials, email, copyright } = footer

    return (
        <footer className={ `site-footer${page === 'Homepage' ? ' t-light' : ''}` }>
            <section className='section--flex'>
                <div className="site-footer__social">
                    <div>{ labels.socialLinks }</div>
                    <ul>
                        { Object.keys(socials).map((label) => (
                            <li><a href={ socials[label] }>{ label }</a></li>
                        )) }
                    </ul>
                </div>
                <div className="site-footer__info">
                    <div>{ email }</div>
                    <div>{ copyright }</div>
                </div>
            </section>
        </footer>
    );
}

export default Footer
