import React from 'react'
import PropTypes from 'prop-types'
import { Slide, Fade } from 'react-reveal';

const Image = ({ baseClass, image={}, alt, revealEffect, revealOptions }) => {
    const RevealTag = revealEffect || 'div';

    return (
        <RevealTag { ...revealOptions } >
            <img
                className={ `${baseClass}__image` }
                srcSet={ `
                    ${image.imageS} 768w,
                    ${image.imageM} 1200w,
                    ${image.imageL} 1440w,
                ` }
                sizes="(max-width: 768px) 700px,
                    (max-width: 1200px) 1000px,
                    1440px"
                src={ image.imageL }
                alt={ alt }
            />
        </RevealTag>
    )
}

Image.defaultProps = {
    image: {},
    alt: '',
    baseClass: '',
}

Image.propTypes = {
    image: PropTypes.shape({}),
    alt: PropTypes.string,
    baseClass: PropTypes.string,
}

export default Image;
