import React, { createContext } from 'react';
import App, { Container } from 'next/app';
import config from 'react-reveal/globals';
import Head from '../components/Head';
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import '../scss/style.scss';
import data from '../data.json'

config({ ssrFadeout: true, forever: true, fraction: 1, distance: '300%' });

export const DataContext = createContext();
class MyApp extends App {
    static async getInitialProps({ Component, ctx }) {
        // const pageProps = await Component.getInitialProps(ctx);
        // return { pageProps };

        // const res = await fetch('http://localhost:8888/wordpress/wp-json/wp/v2/posts?category=home', {
        //   method: 'get',
        //   mode: 'no-cors',
        // })
        // const json = await res.json()

        return { data }
    }

    render() {
        const { Component, data } = this.props;
        const page = Component.name.toLowerCase()
        const bgClass = page === 'shop' ? 'bg-light' : 'bg-dark'

        return (
            <Container>
                <DataContext.Provider value={ { ...data, page } }>
                    <div className={ `page page--${page} ${bgClass}`}>
                        <Head />
                        <Navigation />
                        <Component />
                        <Footer />
                    </div>
                </DataContext.Provider>
            </Container>
        );
    }
}

export default MyApp;


