import React, { useContext } from 'react'
import PropTypes from 'prop-types'
import { DataContext } from '../pages/_app'
import Image from './Image'

const Post = ({ type, textColor, headings, description, quotes, footnote, vectorImage, image }) => {
    const { heroImage } = useContext(DataContext)
    const renderHeadings = () => headings.map((h, i) =>
        <h3 key={ `post-h-${type}-${i}` } className={ `t-6 ${textColor || 't-gold'}` }>{ h }</h3>
    )
    const renderDescriptions = () => description.map((d, i) => 
        <p key={ `post-p-${type}-${i}` } className="t-8">{ d }</p>
    )

    return (
        <div className={`post post--${type}`}>
            { type === 'hobby' && headings && !!headings.length && renderHeadings() }
            <div className='post__content'>
                    <div className="post__text">
                        { type !== 'hobby' && headings && !!headings.length && renderHeadings() }
                        { description && !!description.length && renderDescriptions() }
                        { quotes && !!quotes.length &&
                            quotes.map((q, i) => 
                                <p key={ `post-q-${type}-${i}` } className="t-9">{ q }</p>
                            )
                        }
                    </div>

                    { 
                        vectorImage ?
                            <img className="post__image" src={ vectorImage } />
                        : type === 'passion' ?
                            <Image image={ heroImage } baseClase="post" />
                        : image && image.imageS ?
                            <Image image={  image } baseClase="post" />
                        : null
                    }
            </div> 
            { footnote && <div className='post--footnote t-6'>{ footnote }</div> }
        </div>
    )
}

export default Post
