import React, { useContext } from 'react'
import { DataContext, LabelsContext } from '../pages/_app';

const Footer = () => {
    const { socials, email, copyright } = useContext(DataContext).footer
    const labels = useContext(LabelsContext)

    const onClick = () => {
        if (isOpen) { 
            enableScrolling()
        } else {
            disableScrolling()
        }

        toggleMenu(!isOpen)
    }

    return (
        <footer className="site-footer">
            <div className="site-footer__social">
                <div>{ labels.socialLinks }</div>
                <ul>
                    { Object.keys(socials).map((label) => (
                        <li><a href={ socials[label] }>{ label }</a></li>
                    )) }
                </ul>
            </div>
            <div className="site-footer__info">
                <div>{ email }</div>
                <div>{ copyright }</div>
            </div>
        </footer>
    );
}

export default Footer
