import React, { useState, useEffect, useRef } from 'react'

const createObserver = (el, handleIntersect) => {
    const options = {
        root: null,
        rootMargin: "0px",
        threshold: 0.6
    };
    const observer = new IntersectionObserver(handleIntersect, options);

    observer.observe(el);
}

const Section = ({ baseClass, titleClass, title, children, flexHeight, name }) => {
    const [reveal, setReveal] = useState(false)
    const [scroll, setScroll] = useState({ y: 0, scrollUp: false })
    const sectionRef = useRef()

    useEffect(() => {
        if (sectionRef.current && name === 'statistics') {
            createObserver(sectionRef.current, (entries) => {
                const { isIntersecting, boundingClientRect } = entries[0]
                const { y } = boundingClientRect

                setReveal(isIntersecting)
                setScroll(prevState => {
                    console.log(prevState.y, y, y > prevState.y)
                    return { y, scrollUp: y > prevState.y }
                })
            })
        }
    }, [])

    return (
        <section className={ `${ baseClass }${flexHeight ? ' section--flex' : ''}` } id={ name }>
            <div ref={ sectionRef }>
                { title &&  <h3 className={ `section-title t-6 t-grey ${titleClass}` }>{ title }</h3> }
                { typeof children === 'function' ? children({ reveal, scrollUp: scroll.scrollUp }) : children }
            </div>
        </section>
    )
}

export default Section
