import React, { Fragment, useContext } from 'react'
import { DataContext } from './_app'
import Section from '../components/Section'
import Post from '../components/Post'

const About = () => {
    const content = useContext(DataContext).about
    const sectionKeys = Object.keys(content)

    return (
        <Fragment>
            {
                sectionKeys.map((sectionKey) => {
                    const { title, posts } = content[sectionKey]
                    const bgColor = sectionKey === 'passion' ? ' bg-light' : '';
                    const textColor = sectionKey === 'passion' ? ' t-dark' : sectionKey === 'love' ? ' t-gold' : ''

                    return (
                        <Section
                            key={ sectionKey }
                            title={ title }
                            baseClass={ `${sectionKey}${bgColor}${textColor}` }
                            flexHeight
                        >
                            <div className="post-list">
                                { posts.map((post, i) => 
                                    <Post
                                        { ...post }
                                        key={ `${sectionKey}-${i}` }
                                        type={ sectionKey }
                                        textColor={ textColor }
                                    />
                                ) }
                            </div>
                        </Section>
                    )
                })
            }
            {/* <Section baseClass="about" flexHeight>
                <div className="product-list">
                    { products.map((product) => <ProductItem product={ product } key={ product.name } />) }
                </div>
            </Section> */}
        </Fragment>
    )
}

export default About

