import React, { createContext, useContext } from 'react';
import App, { Container } from "next/app";
import Head from "../components/Head";
import Navigation from "../components/Navigation";
import "../scss/style.scss";
import data from "../data.json";

export const LabelsContext = createContext();
class MyApp extends App {
    static async getInitialProps({ Component, ctx }) {
        const homepageProps = await Component.getInitialProps(ctx);
        return { homepageProps };
    }

    render() {
        const { Component, homepageProps } = this.props;

        return (
            <Container>
                <LabelsContext.Provider value={ homepageProps.labels } >
                    <Head />
                    <Navigation key="navigation" />
                    <Component {...homepageProps} />
                </LabelsContext.Provider>
            </Container>
        );
    }
}

export default MyApp;
