import React from 'react'

const Section = ({ baseClass, titleClass, title, children, flexHeight }) => (
    <section className={ `${ baseClass }${flexHeight ? ' section--flex' : ''}` }>
        { title &&  <h3 className={ `${titleClass} section-title t-6 t-grey` }>{ title }</h3> }
        { children }
    </section>
)

export default Section
