import React, { useContext } from 'react'
import { LabelsContext, DataContext } from '../pages/_app'
import Image from './Image'
import CountBar from './CountBar'

const data = {
    minutes: 253,
    games: 3,
    goals: 1
}

const Fifa = () => {
    const labels = useContext(LabelsContext);
    const { fifa } = useContext(DataContext);

    return (
        <section className="fifa">
            <div className="fifa__top t-gold">
                <div className="t-3 t-outline t-gold">{ labels.fifaWorldCup } 2019</div>
                <div className="t-3">{ labels.statistics }</div>
            </div>
            <p className="fifa__copy">{ fifa.copy }</p>
            <CountBar
                baseClase="fifa"
                items={[
                    { count: data.minutes, labelTop: labels.minutes, labelBottom: labels.played },
                    { count: data.games, labelTop: labels.games, labelBottom: labels.played },
                    { count: data.goals, labelTop: labels.goal, labelBottom: labels.scores },
                ]}
            />
            <Image
                baseClase="fifa"
                imageS = { fifa.imageS }
                imageM = { fifa.imageM }
                imageL = { fifa.imageL }
            />
        </section>
    )
}

export default Fifa;
