import React from 'react'
import PropTypes from 'prop-types'

const Section = ({ baseClase, images, imgAlt, title, children }) => (
    <section className={ baseClase }>
        { title && 
            <h3 className="t-6 t-grey">{ title }</h3>
        }
        <img
            className={ `${baseClase}__image` }
            srcset={ `
                ${images.small} 480w,
                ${images.medium} 768w,
                ${images.large} 1200w
            ` }
            src={ images.large }
            alt={ imgAlt }
        />
        { children }
    </section>
)

export default Section
