import React from 'react'
import Image from './Image'

const ProductItem = ({ product, btnLabel }) => (
    <div className="product-item">
        <div className="product-item__text">
            <h3 className="t-6">{ product.name }</h3>
            <p className="t-8 t-dark">{ product.description }</p>
            <div className="t-6">{ product.price }</div>
            <a className="product-item__button" href={ product.payPalLink}>
                { btnLabel }
            </a>
        </div>
        <Image
            baseClase="product-item"
            imageS = { product.imageS }
            imageM = { product.imageM }
            imageL = { product.imageL }
        />
    </div>
)

export default ProductItem;
