import React from 'react'

const Section = ({ baseClass, titleClass, title, children, flexHeight, name }) => (
    <section className={ `${ baseClass }${flexHeight ? ' section--flex' : ''}` } id={ name }>
        { title &&  <h3 className={ `section-title t-6 t-grey ${titleClass}` }>{ title }</h3> }
        { children }
    </section>
)

export default Section
